section.script:
//
function formatDate(isoString) {
  var date = new Date(isoString);
  // Simpler date format: "Dec 9, 2025"
  return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric' });
}

function loadRelatedPosts() {
  if (!currentLabel) {
    document.getElementById('related-notes-list').innerHTML = '<li class="note-item" style="padding: 12px; text-align:center;">No related notes found.</li>';
    return;
  }

  var feedUrl = '/feeds/posts/summary/-/' + encodeURIComponent(currentLabel) + '?alt=json&max-results=20';

  fetch(feedUrl)
    .then(response => response.json())
    .then(data => {
      var entries = data.feed.entry || [];
      var html = '';

      if (entries.length === 0) {
        html = '<li class="note-item" style="padding: 12px; text-align:center;">No related notes found.</li>';
      } else {
        entries.forEach(entry => {
          var title = entry.title.$t;
          var href = entry.link.find(l => l.rel === 'alternate').href;
          // Parse ID from tag:tag:blogger.com,1999:blog-ID.post-POSTID
          var idRegex = /post-(\d+)/;
          var idMatch = idRegex.exec(entry.id.$t);
          var postId = idMatch ? idMatch[1] : '';

          var isActive = (postId === currentPostId);
          var snippet = entry.summary ? entry.summary.$t.substring(0, 100) + '...' : '';
          var date = formatDate(entry.published.$t);

          html += `
            <li class="note-item ${isActive ? 'active' : ''}">
              <a class="note-item-link" href="${href}">
                <div class="note-item-content">
                  <div class="note-header">
                    <h3 class="note-title">${title}</h3>
                    <span class="note-time">${date}</span>
                  </div>
                  <p class="note-preview">${snippet}</p>
                </div>
              </a>
            </li>
          `;
        });
      }
      document.getElementById('related-notes-list').innerHTML = html;
    })
    .catch(error => {
      console.error('Error fetching related posts:', error);
      document.getElementById('related-notes-list').innerHTML = '<li class="note-item" style="padding: 12px; color:red;">Error loading notes.</li>';
    });
}

// Execute
loadRelatedPosts();

// Share Function
function shareNote() {
  const title = document.title;
  const url = window.location.href;

  if (navigator.share) {
    navigator.share({
      title: title,
      url: url
    }).catch(console.error);
  } else {
    // Fallback to clipboard
    navigator.clipboard.writeText(url).then(() => {
      alert('Link copied to clipboard');
    }).catch(err => {
      console.error('Could not copy text: ', err);
    });
  }
}
